import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Card } from 'react-bootstrap';
import { motion } from 'framer-motion';
import './About.css';
import { FaAward, FaUsers, FaLightbulb, FaHeart } from 'react-icons/fa';

const About = () => {
  const [activeTab, setActiveTab] = useState('about');
  const [isVisible, setIsVisible] = useState(false);
  const [counter, setCounter] = useState({
    students: 0,
    instructors: 0,
    courses: 0,
    successRate: 0
  });

  useEffect(() => {
    // Start the counter animation when component mounts
    setIsVisible(true);

    // Animate counter
    const duration = 2000; // 2 seconds
    const interval = 20; // Update every 20ms
    const steps = duration / interval;

    let currentStep = 0;

    const timer = setInterval(() => {
      currentStep++;

      setCounter({
        students: Math.ceil((currentStep / steps) * 10000),
        instructors: Math.ceil((currentStep / steps) * 50),
        courses: Math.ceil((currentStep / steps) * 120),
        successRate: Math.ceil((currentStep / steps) * 95)
      });

      if (currentStep >= steps) {
        clearInterval(timer);
      }
    }, interval);

    return () => clearInterval(timer);
  }, []);

  // Animation variants
  const fadeIn = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.6
      }
    }
  };

  const staggerContainer = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const scaleIn = {
    hidden: { scale: 0.8, opacity: 0 },
    visible: { 
      scale: 1, 
      opacity: 1,
      transition: {
        duration: 0.5
      }
    }
  };

  // Team members data
  const teamMembers = [
      {
        id: 1,
        name: 'Mr. Rajeev Kumar',
        role: 'Founder & Managing Director',
        image: 'https://img.freepik.com/free-photo/indian-businessman-with-his-white-presentation-board_496169-583.jpg?w=1380&t=st=1709029005~exp=1709029605~hmac=ece9bd5d2efe9babffc82fd3a5d1af7fd9c49a2fbffee2f26c46a04547e5d5bd',
        bio: 'Mr. Rajeev Kumar has revolutionized medical education in India with over 15 years of experience in the education sector. He founded FIGS with a vision to provide quality training to medical students preparing for their exams.',
        specialization: 'Education Management',
        education: 'MBA (IIM Bangalore)',
        awards: ['Education Entrepreneur of the Year 2022', 'Innovation in Education Award 2020']
      },
      {
        id: 2,
        name: 'Dr. Sunil Verma',
        role: 'NEXT Specialist & Lead Instructor',
        image: 'https://img.freepik.com/free-photo/pleased-young-male-doctor-wearing-medical-robe-stethoscope-showing-thumb-up_409827-2249.jpg',
        bio: 'Dr. Verma specializes in preparing students for the NEXT exam with his comprehensive knowledge of the curriculum and experience as an examiner.',
        specialization: 'Internal Medicine',
        education: 'MBBS, MD (PGIMER Chandigarh)',
        awards: ['Outstanding Contribution to Medical Education 2021']
      },
      {
        id: 3,
        name: 'Dr. Priya Patel',
        role: 'Clinical Skills Trainer',
        image: 'https://img.freepik.com/free-photo/indian-young-female-doctor-white-coat-with-stethoscope-posing-smiling_651396-2211.jpg',
        bio: 'Dr. Patel focuses on teaching clinical skills and practical examination techniques to ensure students are well-prepared for their clinical assessments.',
        specialization: 'Surgery',
        education: 'MBBS, MS (KEM Hospital, Mumbai)',
        awards: ['Excellence in Clinical Teaching 2019', 'Best Medical Educator 2021']
      },
      {
        id: 4,
        name: 'Dr. Amit Desai',
        role: 'Medical Education Researcher',
        image: 'https://img.freepik.com/free-photo/portrait-smiling-indian-doctor-man-with-stethoscope-white-coat_496169-426.jpg',
        bio: 'With a PhD in Medical Education, Dr. Desai develops innovative teaching methodologies and creates comprehensive study materials for our students.',
        specialization: 'Medical Education',
        education: 'MBBS, PhD in Medical Education (JIPMER Pondicherry)',
        awards: ['Research Excellence Award 2022', 'Innovation in Medical Education 2020']
      }
  ];

  // Mission data
  const missionPoints = [
    {
      icon: 'fa-graduation-cap',
      title: 'Comprehensive Learning',
      description: 'Provide comprehensive, up-to-date educational resources that prepare students for success'
    },
    {
      icon: 'fa-hands-helping',
      title: 'Personalized Support',
      description: 'Offer personalized guidance and support to help each student achieve their full potential'
    },
    {
      icon: 'fa-chart-line',
      title: 'Measurable Outcomes',
      description: 'Focus on measurable improvements in student performance and exam success rates'
    },
    {
      icon: 'fa-handshake',
      title: 'Industry Partnerships',
      description: 'Foster partnerships with medical institutions to align education with real-world requirements'
    }
  ];

  const milestones = [
    {
      year: 2025,
      title: 'Founded in Noida',
      description: 'FIGS (Future Indo Global Service) was established in Noida as our parent domain, with a vision to provide exceptional services across multiple sectors.',
      icon: 'fa-building'
    },
    {
      year: 2025,
      title: 'SMC (Search My College) Launch',
      description: 'Launched our first platform SMC (Search My College), providing comprehensive career counseling for students seeking education in India and abroad.',
      icon: 'fa-graduation-cap'
    },
    {
      year: 2025,
      title: 'SMT (Search My Trips) Launch',
      description: 'Expanded our services with SMT (Search My Trips), offering specialized travel and tourism solutions to customers across India.',
      icon: 'fa-plane'
    },
    {
      year: 2025,
      title: 'SMM (Search My Medical) Launch',
      description: 'Introduced SMM (Search My Medical), our dedicated platform providing a wide range of medical-related services including NEXT exam preparation and medical education.',
      icon: 'fa-heartbeat'
    },
    {
      year: 2025,
      title: 'Online Platform Integration',
      description: 'Successfully integrated all three platforms under the FIGS parent domain, creating a comprehensive ecosystem of educational, travel, and medical services.',
      icon: 'fa-globe'
    },
    {
      year: 2025,
      title: 'Pan-India Expansion Vision',
      description: 'Set our vision for expansion across major Indian cities with plans to establish physical centers for each of our service domains.',
      icon: 'fa-map-marker-alt'
    }
  ];

  const testimonials = [
      {
        id: 1,
      name: 'Dr. Vikram Singh',
      role: 'Former Student, Now Practicing Physician at AIIMS',
      content: 'The NExT preparation course was exactly what I needed. The instructors are knowledgeable and the practice questions were very similar to the actual exam. I credit my success to FIGS Medical Education.',
      image: 'https://img.freepik.com/free-photo/indian-doctor-portrait_107420-67919.jpg',
        rating: 5
      },
      {
        id: 2,
      name: 'Dr. Priya Sharma',
      role: 'Recent Medical Graduate from Grant Medical College',
      content: 'I had struggled with clinical assessments, but after taking the clinical skills course, I passed with confidence. The personalized feedback and hands-on practice sessions made all the difference in my performance.',
      image: 'https://img.freepik.com/free-photo/indian-young-female-doctor-white-coat-with-stethoscope-posing-smiling_651396-2211.jpg',
        rating: 5
      },
      {
        id: 3,
      name: 'Rahul Mehta',
      role: 'Final Year Medical Student at KEM Hospital',
      content: 'The online platform is user-friendly and the content is comprehensive. The mock exams really helped me identify my weak areas and focus my studying. I particularly appreciated the detailed explanations for each question.',
      image: 'https://img.freepik.com/free-photo/medical-student-wearing-white-coat_23-2148519043.jpg',
        rating: 4
      },
      {
        id: 4,
      name: 'Dr. Anjali Gupta',
      role: 'International Medical Graduate from Maharashtra',
      content: 'As an IMG, I found the courses specifically designed for international students very helpful. The instructors understand our unique challenges and provided targeted strategies to overcome them. I can proudly say I am now licensed to practice in the US.',
      image: 'https://img.freepik.com/free-photo/young-female-doctor-lab-coat-with-stethoscope-posing-confident_176474-8415.jpg',
        rating: 5
      }
  ];

  const renderStars = (rating) => {
    return Array.from({ length: 5 }).map((_, index) => (
        <i 
        key={index} 
        className={`fas fa-star ${index < rating ? 'filled' : 'empty'}`}
        ></i>
    ));
  };

  return (
    <div className="about-page">
      {/* Hero Section */}
      <section className="about-hero-section">
        <div className="about-hero-overlay"></div>
        <Container>
          <motion.div
            initial="hidden"
            animate="visible"
            variants={staggerContainer}
            className="about-hero-content"
          >
            <motion.h1 
              className="about-hero-title text-white"
              variants={fadeIn}
            >
              About <span className="text-highlight">FIGS</span> <span className="text-gradient-white">Medical Education</span>
            </motion.h1>
            <motion.p 
              className="about-hero-subtitle"
              variants={fadeIn}
            >
              Empowering the next generation of medical professionals through innovative education and personalized learning
            </motion.p>
            <motion.div 
              className="about-hero-buttons"
              variants={fadeIn}
            >
              <a href="#mission" className="btn btn-primary me-3">Our Mission</a>
              <a href="#team" className="btn btn-outline-light">Our Team</a>
            </motion.div>
          </motion.div>
        </Container>
        <div className="about-hero-shape-1"></div>
        <div className="about-hero-shape-2"></div>
      </section>

      {/* Stats Counter Section */}
      <section className="about-stats-section">
        <Container>
          <Row>
            <Col md={3} sm={6} className="mb-4 mb-md-0">
              <motion.div 
                className="about-stat-card"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: isVisible ? 1 : 0.8, opacity: isVisible ? 1 : 0 }}
                transition={{ duration: 0.5, delay: 0.1 }}
              >
                <div className="about-stat-icon">
                  <i className="fas fa-user-graduate"></i>
                </div>
                <h3 className="about-stat-number">{counter.students.toLocaleString()}+</h3>
                <p className="about-stat-title">Students Trained</p>
              </motion.div>
            </Col>

            <Col md={3} sm={6} className="mb-4 mb-md-0">
              <motion.div 
                className="about-stat-card"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: isVisible ? 1 : 0.8, opacity: isVisible ? 1 : 0 }}
                transition={{ duration: 0.5, delay: 0.2 }}
              >
                <div className="about-stat-icon">
                  <i className="fas fa-chalkboard-teacher"></i>
                </div>
                <h3 className="about-stat-number">{counter.instructors}+</h3>
                <p className="about-stat-title">Expert Instructors</p>
              </motion.div>
            </Col>

            <Col md={3} sm={6} className="mb-4 mb-md-0">
              <motion.div 
                className="about-stat-card"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: isVisible ? 1 : 0.8, opacity: isVisible ? 1 : 0 }}
                transition={{ duration: 0.5, delay: 0.3 }}
              >
                <div className="about-stat-icon">
                  <i className="fas fa-book-medical"></i>
                </div>
                <h3 className="about-stat-number">{counter.courses}+</h3>
                <p className="about-stat-title">Specialized Courses</p>
              </motion.div>
            </Col>

            <Col md={3} sm={6}>
              <motion.div 
                className="about-stat-card"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: isVisible ? 1 : 0.8, opacity: isVisible ? 1 : 0 }}
                transition={{ duration: 0.5, delay: 0.4 }}
              >
                <div className="about-stat-icon">
                  <i className="fas fa-chart-line"></i>
                </div>
                <h3 className="about-stat-number">{counter.successRate}%</h3>
                <p className="about-stat-title">Success Rate</p>
              </motion.div>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Who We Are Section */}
      <section id="about" className="about-main-section">
        <Container>
          <Row className="align-items-center">
            <Col lg={6} className="mb-5 mb-lg-0">
              <motion.div
