import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaPhoneAlt, FaEnvelope, FaClock, FaSpinner, 
         FaPaperPlane, FaMapMarkedAlt, FaCheckCircle, FaExclamationCircle,
         FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn, FaYoutube } from 'react-icons/fa';
import '../styles/Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: ''
  });
  
  const [formStatus, setFormStatus] = useState({
    message: '',
    type: '', // 'success' or 'error'
    loading: false
  });

  const [formErrors, setFormErrors] = useState({});
  const [touched, setTouched] = useState({});

  // Validate form on input change when field has been touched
  useEffect(() => {
    if (Object.keys(touched).length > 0) {
      validateForm();
    }
  }, [formData, touched]);

  const validateForm = () => {
    const errors = {};
    
    if (touched.name && !formData.name.trim()) {
      errors.name = 'Name is required';
    }
    
    if (touched.email && !formData.email) {
      errors.email = 'Email is required';
    } else if (touched.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email is invalid';
    }
    
    if (touched.subject && !formData.subject.trim()) {
      errors.subject = 'Subject is required';
    }
    
    if (touched.message && !formData.message.trim()) {
      errors.message = 'Message is required';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched(prev => ({
      ...prev,
      [name]: true
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Mark all fields as touched to show all validation errors
    const allTouched = Object.keys(formData).reduce((acc, field) => {
      acc[field] = true;
      return acc;
    }, {});
    
    setTouched(allTouched);
    
    const isValid = validateForm();
    
    if (!isValid) {
      return;
    }
    
    setFormStatus({
      message: '',
      type: '',
      loading: true
    });
    
    // Simulate API call
    try {
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Success response simulation
      setFormStatus({
        message: 'Your message has been sent successfully! We will get back to you soon.',
        type: 'success',
        loading: false
      });
      
      // Reset form
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: '',
        message: ''
      });
      setTouched({});
      
      // Reset success message after 5 seconds
      setTimeout(() => {
        setFormStatus({
          message: '',
          type: '',
          loading: false
        });
      }, 5000);
      
    } catch (error) {
      setFormStatus({
        message: 'Something went wrong. Please try again later.',
        type: 'error',
        loading: false
      });
    }
  };

  return (
    <div className="contact-page">
      {/* Hero Section */}
      <section className="contact-hero">
        <div className="container">
          <motion.div 
            className="hero-content"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <h1>Get in Touch <span className="highlight">With Us</span></h1>
            <p>Have questions about our services or need help? We're here to assist you. Reach out to us through any of the channels below.</p>
          </motion.div>
        </div>
      </section>

      {/* Contact Info Section */}
      <section className="contact-info-section">
        <motion.div 
          className="contact-info-grid"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, staggerChildren: 0.1 }}
        >
          <motion.div 
            className="contact-info-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            whileHover={{ y: -5, boxShadow: '0 10px 25px rgba(0,0,0,0.1)' }}
          >
            <div className="info-icon">
              <FaMapMarkerAlt />
            </div>
            <h3>Our Location</h3>
            <p>123 Education Street</p>
            <p>Learning District</p>
            <p>New Delhi, 110001</p>
          </motion.div>
          
          <motion.div 
            className="contact-info-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: 0.1 }}
            whileHover={{ y: -5, boxShadow: '0 10px 25px rgba(0,0,0,0.1)' }}
          >
            <div className="info-icon">
              <FaPhoneAlt />
            </div>
            <h3>Phone Number</h3>
            <p>+91 98765 43210</p>
            <p>+91 12345 67890</p>
            <p>Mon-Fri, 9:00 AM - 6:00 PM</p>
          </motion.div>
          
          <motion.div 
            className="contact-info-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: 0.2 }}
            whileHover={{ y: -5, boxShadow: '0 10px 25px rgba(0,0,0,0.1)' }}
          >
            <div className="info-icon">
              <FaEnvelope />
            </div>
            <h3>Email Address</h3>
            <p>contact@figsedu.com</p>
            <p>support@figsedu.com</p>
            <p>We respond within 24 hours</p>
          </motion.div>
          
          <motion.div 
            className="contact-info-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: 0.3 }}
            whileHover={{ y: -5, boxShadow: '0 10px 25px rgba(0,0,0,0.1)' }}
          >
            <div className="info-icon">
              <FaClock />
            </div>
            <h3>Working Hours</h3>
            <p>Monday - Friday</p>
            <p>9:00 AM - 6:00 PM</p>
            <p>Closed on weekends & holidays</p>
          </motion.div>
        </motion.div>
      </section>

      {/* Contact Form and Map Section */}
      <section className="contact-form-section">
        <div className="contact-grid">
          <motion.div 
            className="contact-form-container"
            initial={{ opacity: 0, x: -30 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            id="contact-form"
          >
            <h2>Send Us a Message</h2>
            <p>Feel free to reach out to us with any questions, feedback, or inquiries. We'll get back to you as soon as possible.</p>
            
            {formStatus.message && (
              <motion.div 
                className={`alert ${formStatus.type === 'success' ? 'alert-success' : 'alert-error'}`}
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
              >
                {formStatus.type === 'success' ? <FaCheckCircle /> : <FaExclamationCircle />}
                {formStatus.message}
              </motion.div>
            )}
            
            <form className="contact-form" onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="name">Full Name*</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className={formErrors.name && touched.name ? 'error' : ''}
                    placeholder="Your full name"
                  />
                  {formErrors.name && touched.name && (
                    <div className="error-message">{formErrors.name}</div>
                  )}
                </div>
                
                <div className="form-group">
                  <label htmlFor="email">Email Address*</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className={formErrors.email && touched.email ? 'error' : ''}
                    placeholder="Your email address"
                  />
                  {formErrors.email && touched.email && (
                    <div className="error-message">{formErrors.email}</div>
                  )}
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="phone">Phone Number</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    placeholder="Your phone number (optional)"
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="subject">Subject*</label>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className={formErrors.subject && touched.subject ? 'error' : ''}
                    placeholder="Message subject"
                  />
                  {formErrors.subject && touched.subject && (
                    <div className="error-message">{formErrors.subject}</div>
                  )}
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="message">Your Message*</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  className={formErrors.message && touched.message ? 'error' : ''}
                  rows="6"
                  placeholder="Type your message here..."
                ></textarea>
                {formErrors.message && touched.message && (
                  <div className="error-message">{formErrors.message}</div>
                )}
              </div>
              
              <motion.button 
                type="submit" 
                className="btn-primary" 
                disabled={formStatus.loading}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                {formStatus.loading ? (
                  <>
                    <FaSpinner className="fa-spin" />
                    Sending...
                  </>
                ) : (
                  <>
                    <FaPaperPlane />
                    Send Message
                  </>
                )}
              </motion.button>
            </form>
          </motion.div>
          
          <motion.div 
            className="contact-map"
            initial={{ opacity: 0, x: 30 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <h2>Find Us</h2>
            <div className="map-container">
              <div className="map-placeholder">
                <FaMapMarkedAlt />
                <p>Interactive Map Coming Soon</p>
                <p>123 Education Street, Learning District, New Delhi</p>
              </div>
            </div>
            
            <div className="social-links">
              <h3>Connect With Us</h3>
              <div className="social-icons">
                <motion.a 
                  href="#" 
                  className="social-icon"
                  whileHover={{ y: -5, backgroundColor: '#3b5998', color: '#fff' }}
                >
                  <FaFacebookF />
                </motion.a>
                <motion.a 
                  href="#" 
                  className="social-icon"
                  whileHover={{ y: -5, backgroundColor: '#1da1f2', color: '#fff' }}
                >
                  <FaTwitter />
                </motion.a>
                <motion.a 
                  href="#" 
                  className="social-icon"
                  whileHover={{ y: -5, backgroundColor: '#e1306c', color: '#fff' }}
                >
                  <FaInstagram />
                </motion.a>
                <motion.a 
                  href="#" 
                  className="social-icon"
                  whileHover={{ y: -5, backgroundColor: '#0077b5', color: '#fff' }}
                >
                  <FaLinkedinIn />
                </motion.a>
                <motion.a 
                  href="#" 
                  className="social-icon"
                  whileHover={{ y: -5, backgroundColor: '#ff0000', color: '#fff' }}
                >
                  <FaYoutube />
                </motion.a>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* FAQ Section */}
      <section className="faq-section">
        <div className="container">
          <motion.div 
            className="section-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <h2>Frequently Asked Questions</h2>
            <p>Find answers to common questions about our services</p>
          </motion.div>
          
          <div className="faq-grid">
            <motion.div 
              className="faq-item"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.1 }}
              viewport={{ once: true }}
              whileHover={{ boxShadow: '0 8px 30px rgba(0,0,0,0.12)' }}
            >
              <h3>How quickly can I expect a response?</h3>
              <p>We aim to respond to all inquiries within 24-48 business hours. For urgent matters, we recommend calling our support line directly.</p>
            </motion.div>
            
            <motion.div 
              className="faq-item"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.2 }}
              viewport={{ once: true }}
              whileHover={{ boxShadow: '0 8px 30px rgba(0,0,0,0.12)' }}
            >
              <h3>Do you offer online support?</h3>
              <p>Yes, we provide online chat support during our working hours. You can also email us anytime and we'll get back to you as soon as possible.</p>
            </motion.div>
            
            <motion.div 
              className="faq-item"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.3 }}
              viewport={{ once: true }}
              whileHover={{ boxShadow: '0 8px 30px rgba(0,0,0,0.12)' }}
            >
              <h3>How can I schedule a meeting?</h3>
              <p>You can request a meeting by filling out the contact form above or by calling our office. We offer both in-person and virtual meetings during business hours.</p>
            </motion.div>
            
            <motion.div 
              className="faq-item"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.4 }}
              viewport={{ once: true }}
              whileHover={{ boxShadow: '0 8px 30px rgba(0,0,0,0.12)' }}
            >
              <h3>Where can I get technical support?</h3>
              <p>For technical support related to our educational platform, please email support@figsedu.com with your student ID and a detailed description of the issue.</p>
            </motion.div>
          </div>
          
          <motion.div 
            className="cta-container"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.5 }}
            viewport={{ once: true }}
          >
            <p>Can't find an answer to your question?</p>
            <motion.a 
              href="#contact-form" 
              className="btn-secondary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Contact Us Now
            </motion.a>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Contact;
