import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Badge } from '../Shared';
import nextService from '../../services/nextService';
import './NextCourseSection.css';

const mockCourses = [
  {
    id: 1,
    title: 'Comprehensive NExT Step 1 Preparation',
    description: 'Master the theoretical foundations with our comprehensive Step 1 program. Covers all subjects with integrated learning approaches and regular assessments.',
    image: 'https://img.freepik.com/free-photo/medical-student-studying-with-tablet_23-2149506431.jpg',
    duration: '6 months',
    enrolled: 1250,
    price: 49999,
    discountedPrice: 39999,
    isFeatured: true
  },
  {
    id: 2,
    title: 'NExT Step 2 Clinical Skills Program',
    description: 'Develop advanced clinical competencies through hands-on training, simulated patient interactions, and structured clinical rotations.',
    image: 'https://img.freepik.com/free-photo/medical-workers-studying-scanning-results_23-2149506514.jpg',
    duration: '4 months',
    enrolled: 875,
    price: 44999,
    discountedPrice: null,
    isFeatured: false
  },
  {
    id: 3,
    title: 'NExT Fast Track Revision Course',
    description: 'Accelerated review program focusing on high-yield topics, exam strategies, and rapid revision techniques for optimal exam preparation.',
    image: 'https://img.freepik.com/free-photo/medical-students-studying-together_23-2149506528.jpg',
    duration: '2 months',
    enrolled: 2100,
    price: 24999,
    discountedPrice: 19999,
    isFeatured: true
  },
  {
    id: 4,
    title: 'Subject-wise NExT Preparation',
    description: 'Specialized modules for in-depth subject mastery, featuring detailed concept coverage and subject-specific assessment strategies.',
    image: 'https://img.freepik.com/free-photo/medical-students-having-online-class_23-2149506498.jpg',
    duration: 'Varies',
    enrolled: 1560,
    price: 34999,
    discountedPrice: null,
    isFeatured: false
  }
];

const NextCourseSection = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        setCourses(mockCourses);
        setLoading(false);
      } catch (err) {
        setError('Failed to load courses. Please try again later.');
        setLoading(false);
        console.error('Error fetching NExT courses:', err);
      }
    };

    fetchCourses();
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate');
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.2 }
    );

    document.querySelectorAll('.course-card').forEach((card) => {
      observer.observe(card);
    });

    return () => observer.disconnect();
  }, [courses]);

  if (loading) {
    return <div className="next-courses-loading">Loading courses...</div>;
  }

  if (error) {
    return <div className="next-courses-error">{error}</div>;
  }

  return (
    <section className="next-courses-section">
      <div className="container">
        <div className="section-header">
          <h2>Our NExT Preparation Courses</h2>
          <p>
            Embark on your journey to medical excellence with our comprehensive NExT preparation courses. 
            Each program is carefully designed to provide you with the knowledge, skills, and confidence 
            needed to excel in the National Exit Test. Choose from our range of specialized courses 
            tailored to meet your specific learning needs and goals.
          </p>
        </div>

        <div className="courses-grid">
          {courses.map(course => (
            <div className="course-card" key={course.id}>
              <div className="course-image">
                <img src={course.image} alt={course.title} />
                {course.isFeatured && (
                  <Badge text="Featured" color="warning" size="md" className="course-badge" />
                )}
              </div>
              <div className="course-content">
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <div className="course-details">
                  <div className="course-info">
                    <span><i className="fas fa-clock"></i> {course.duration}</span>
                    <span><i className="fas fa-users"></i> {course.enrolled} enrolled</span>
                  </div>
                  <div className="course-price">
                    {course.discountedPrice ? (
                      <>
                        <span className="original-price">₹{course.price.toLocaleString()}</span>
                        <span className="discounted-price">₹{course.discountedPrice.toLocaleString()}</span>
                      </>
                    ) : (
                      <span className="price">₹{course.price.toLocaleString()}</span>
                    )}
                  </div>
                </div>
                <Link to={`/courses/${course.id}`} className="btn-course">
                  View Details
                </Link>
              </div>
            </div>
          ))}
        </div>

        <div className="courses-cta">
          <Link to="/next/all-courses" className="btn-secondary">
            View All Courses
          </Link>
        </div>
      </div>
    </section>
  );
};

export default NextCourseSection;
