import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Badge } from '../Shared';
import nextService from '../../services/nextService';
import './NextCourseSection.css';

const NextCourseSection = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        // In a real application, uncomment this to fetch from API
        // const data = await nextService.getCourses();
        // setCourses(data);
        
        // For demo purposes, using static data
        setCourses(mockCourses);
        setLoading(false);
      } catch (err) {
        setError('Failed to load courses. Please try again later.');
        setLoading(false);
        console.error('Error fetching NExT courses:', err);
      }
    };

    fetchCourses();
  }, []);

  if (loading) {
    return <div className="next-courses-loading">Loading courses...</div>;
  }

  if (error) {
    return <div className="next-courses-error">{error}</div>;
  }

  return (
    <section className="next-courses-section">
      <div className="container">
        <div className="section-header">
          <h2>Our NExT Preparation Courses</h2>
          <p>Comprehensive courses designed to help you excel in the National Exit Test</p>
        </div>

        <div className="courses-grid">
          {courses.map(course => (
            <div className="course-card" key={course.id}>
              <div className="course-image">
                <img src={course.image} alt={course.title} />
                {course.isFeatured && (
                  <Badge text="Featured" color="warning" size="md" className="course-badge" />
                )}
              </div>
              <div className="course-content">
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <div className="course-details">
                  <div className="course-info">
                    <span><i className="fas fa-clock"></i> {course.duration}</span>
                    <span><i className="fas fa-users"></i> {course.enrolled} enrolled</span>
                  </div>
                  <div className="course-price">
                    {course.discountedPrice ? (
                      <>
                        <span className="original-price">₹{course.price}</span>
                        <span className="discounted-price">₹{course.discountedPrice}</span>
                      </>
                    ) : (
                      <span className="price">₹{course.price}</span>
                    )}
                  </div>
                </div>
                <Link to={`/courses/${course.id}`} className="btn-course">
                  View Details
                </Link>
              </div>
            </div>
          ))}
        </div>

        <div className="courses-cta">
          <Link to="/next/all-courses" className="btn-secondary">
            View All Courses
          </Link>
        </div>
      </div>
    </section>
  );
};

// Mock data for demonstration
const mockCourses = [
  {
    id: 1,
    title: 'Comprehensive NExT Step 1 Preparation',
    description: 'Complete preparation covering all theory subjects and concepts required for NExT Step 1.',
    image: '/images/courses/next-step1.jpg',
    duration: '6 months',
    enrolled: 1250,
    price: 49999,
    discountedPrice: 39999,
    isFeatured: true
  },
  {
    id: 2,
    title: 'NExT Step 2 Clinical Skills Program',
    description: 'Intensive training for clinical skills assessment with hands-on practice and simulations.',
    image: '/images/courses/next-step2.jpg',
    duration: '4 months',
    enrolled: 875,
    price: 44999,
    discountedPrice: null,
    isFeatured: false
  },
  {
    id: 3,
    title: 'NExT Fast Track Revision Course',
    description: 'Quick revision of high-yield topics and exam strategies for last-minute preparation.',
    image: '/images/courses/next-fasttrack.jpg',
    duration: '2 months',
    enrolled: 2100,
    price: 24999,
    discountedPrice: 19999,
    isFeatured: true
  },
  {
    id: 4,
    title: 'Subject-wise NExT Preparation',
    description: 'Focused preparation for individual subjects with in-depth coverage of key concepts.',
    image: '/images/courses/next-subjects.jpg',
    duration: 'Varies',
    enrolled: 1560,
    price: 34999,
    discountedPrice: null,
    isFeatured: false
  }
];

export default NextCourseSection;
