const AllCoursesPage = () => {
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [filters, setFilters] = useState({
    search: '',
    category: 'all',
    duration: 'all',
    priceRange: 'all'
  });

  const coursesPerPage = 12;

  useEffect(() => {
    // Simulating API call
    setCourses(allCourses);
    setFilteredCourses(allCourses);
  }, []);

  useEffect(() => {
    applyFilters();
  }, [filters, courses]);

  const applyFilters = () => {
    let filtered = [...courses];

    // Apply search filter
    if (filters.search) {
      filtered = filtered.filter(course =>
        course.title.toLowerCase().includes(filters.search.toLowerCase()) ||
        course.description.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Apply category filter
    if (filters.category !== 'all') {
      filtered = filtered.filter(course => course.category === filters.category);
    }

    // Apply duration filter
    if (filters.duration !== 'all') {
      filtered = filtered.filter(course => {
        const duration = parseInt(course.duration);
        switch (filters.duration) {
          case 'short': return duration <= 2;
          case 'medium': return duration > 2 && duration <= 4;
          case 'long': return duration > 4;
          default: return true;
        }
      });
    }

    // Apply price range filter
    if (filters.priceRange !== 'all') {
      filtered = filtered.filter(course => {
        const price = course.discountedPrice || course.price;
        switch (filters.priceRange) {
          case 'low': return price <= 20000;
          case 'medium': return price > 20000 && price <= 40000;
          case 'high': return price > 40000;
          default: return true;
        }
      });
    }

    setFilteredCourses(filtered);
    setCurrentPage(1); // Reset to first page when filters change
  };

  const handleFilterChange = (filterName, value) => {
    setFilters(prev => ({
      ...prev,
      [filterName]: value
    }));
  };

  // Pagination
  const indexOfLastCourse = currentPage * coursesPerPage;
  const indexOfFirstCourse = indexOfLastCourse - coursesPerPage;
  const currentCourses = filteredCourses.slice(indexOfFirstCourse, indexOfLastCourse);
  const totalPages = Math.ceil(filteredCourses.length / coursesPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="all-courses-page">
      <motion.div 
        className="courses-hero"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        <h1>All NExT Preparation Courses</h1>
        <p>Browse our comprehensive collection of NExT preparation courses designed to help you excel in your medical career.</p>
      </motion.div>

      <div className="courses-container">
        {/* Filters Section */}
        <div className="courses-filters">
          <input
            type="text"
            placeholder="Search courses..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
            className="search-input"
          />
          
          <select
            value={filters.category}
            onChange={(e) => handleFilterChange('category', e.target.value)}
            className="filter-select"
          >
            <option value="all">All Categories</option>
            <option value="Step 1">Step 1</option>
            <option value="Step 2">Step 2</option>
            <option value="Clinical Skills">Clinical Skills</option>
            <option value="Revision">Revision</option>
          </select>

          <select
            value={filters.duration}
            onChange={(e) => handleFilterChange('duration', e.target.value)}
            className="filter-select"
          >
            <option value="all">All Durations</option>
            <option value="short">Short (≤ 2 months)</option>
            <option value="medium">Medium (3-4 months)</option>
            <option value="long">Long (> 4 months)</option>
          </select>

          <select
            value={filters.priceRange}
            onChange={(e) => handleFilterChange('priceRange', e.target.value)}
            className="filter-select"
          >
            <option value="all">All Prices</option>
            <option value="low">₹20,000 & Below</option>
            <option value="medium">₹20,001 - ₹40,000</option>
            <option value="high">Above ₹40,000</option>
          </select>
        </div>

        {/* Courses Grid */}
        <div className="courses-grid">
          {currentCourses.map(course => (
            <motion.div
              key={course.id}
              className="course-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="course-image">
                <img src={course.image} alt={course.title} />
                {course.isFeatured && <div className="course-badge">Featured</div>}
              </div>
              <div className="course-content">
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <div className="course-details">
                  <div className="course-info">
                    <span>
                      <i className="fas fa-clock"></i>
                      Duration: {course.duration}
                    </span>
                    <span>
                      <i className="fas fa-users"></i>
                      {course.enrolled.toLocaleString()} enrolled
                    </span>
                  </div>
                  <div className="course-price">
                    {course.discountedPrice ? (
                      <>
                        <span className="original-price">₹{course.price.toLocaleString()}</span>
                        <span className="discounted-price">₹{course.discountedPrice.toLocaleString()}</span>
                      </>
                    ) : (
                      <span className="price">₹{course.price.toLocaleString()}</span>
                    )}
                  </div>
                  <button className="btn-course">View Details</button>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="pagination">
            <button
              onClick={() => paginate(currentPage - 1)}
              disabled={currentPage === 1}
              className="pagination-button"
            >
              Previous
            </button>
            
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index + 1}
                onClick={() => paginate(index + 1)}
                className={`pagination-button ${currentPage === index + 1 ? 'active' : ''}`}
              >
                {index + 1}
              </button>
            ))}
            
            <button
              onClick={() => paginate(currentPage + 1)}
              disabled={currentPage === totalPages}
              className="pagination-button"
            >
              Next
            </button>
          </div>
        )}

        {filteredCourses.length === 0 && (
          <div className="no-courses">
            <h3>No courses found</h3>
            <p>Try adjusting your filters to find more courses.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AllCoursesPage;
