            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
            className="text-center mb-5"
          >
            <h2 className="section-title">Success Stories</h2>
            <div className="section-title-underline mx-auto"></div>
            <p className="section-subtitle">Hear from our students who have transformed their medical careers with FIGS</p>
          </motion.div>

          <div className="testimonials-carousel">
            <Row>
              {testimonials.map((testimonial, index) => (
                <Col lg={6} className="mb-4" key={testimonial.id}>
                  <motion.div 
                    className="testimonial-card enhanced"
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                    viewport={{ once: true }}
                    whileHover={{ 
                      y: -8,
                      boxShadow: "0 20px 40px rgba(0, 0, 0, 0.15)",
                      transition: { duration: 0.3 }
                    }}
                  >
                <div className="testimonial-content">
                      <div className="testimonial-quote-mark">
                        <i className="fas fa-quote-left"></i>
                      </div>
                      <div className="testimonial-rating">
                    {renderStars(testimonial.rating)}
                  </div>
                  <p className="testimonial-text">"{testimonial.content}"</p>
                </div>
                    <div className="testimonial-footer enhanced">
                      <div className="testimonial-image">
                        <motion.img 
                          src={testimonial.image} 
                          alt={testimonial.name} 
                          whileHover={{ scale: 1.05 }}
                          transition={{ duration: 0.3 }}
                        />
                      </div>
                      <div className="testimonial-info">
                        <h4 className="testimonial-name">{testimonial.name}</h4>
                        <p className="testimonial-role">{testimonial.role}</p>
                      </div>
                      <div className="testimonial-quote-icon">
                        <i className="fas fa-quote-right"></i>
                </div>
              </div>
                  </motion.div>
                </Col>
            ))}
            </Row>
          </div>
        </Container>
      </section>

      {/* Contact CTa */}
      <section className="about-cta-section">
        <Container>
          <Row className="justify-content-center">
            <Col lg={10}>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                viewport={{ once: true }}
                className="about-cta-container"
              >
                <div className="about-cta-content">
                  <h2>Ready to Start Your Medical Education Journey?</h2>
                  <p>Join thousands of Indian medical students who have transformed their learning and careers with FIGS Medical Education. Our expert-led courses, comprehensive resources, and supportive community are waiting for you.</p>
                  <div className="about-cta-buttons">
                    <Link to="/courses" className="btn btn-primary btn-lg">Explore Our Courses</Link>
                    <Link to="/contact" className="btn btn-outline-light btn-lg">Contact Us</Link>
            </div>
          </div>
                <div className="about-cta-shape"></div>
              </motion.div>
            </Col>
          </Row>
        </Container>
      </section>
    </div>
  );
};

export default About;
